(59, 1, '2019-08-04 00:56:11', '2019-08-04 03:56:11', 'Especialista em direito tributário pela Pontifícia Universidade Católica de São Paulo pela PUC/SP.', 'Francisco Augusto Nomura Galucci', '', 'publish', 'closed', 'closed', '', 'francisco-augusto-nomura-galucci', '', '', '2019-08-08 01:15:52', '2019-08-08 04:15:52', '', 0, 'http://localhost/sadimorishita/?post_type=nossos-advogados&#038;p=59', 0, 'nossos-advogados', '', 0),
(60, 1, '2019-08-03 01:03:58', '2019-08-03 04:03:58', 'Especialista em Propriedade Intelectual e Gestão de Negócios pela FGV (Gvlaw) e professor de Propriedade Intelectual na PUC/SP.', 'Lucas Tosetti Silveira', '', 'publish', 'closed', 'closed', '', 'lucas-tosetti-silveira', '', '', '2019-08-08 01:16:25', '2019-08-08 04:16:25', '', 0, 'http://localhost/sadimorishita/?post_type=nossos-advogados&#038;p=60', 0, 'nossos-advogados', '', 0),
